import React, { useState } from "react";
import UserForm from './UserForm';
import { BASEURL } from "../services/api";
// import { navigate } from "@reach/router"; // Import navigation library

export const AddUser = () => {
  const [isLoading, setIsLoading] = useState(false); // Add a loading state

  // // Define a function to handle redirection
  // const redirect = () => {
  //   navigate(`/${TAB_IDS.VIEW_USER}`); // You need to import TAB_IDS if it's not already imported
  // }

  const handleSubmit = (user, { resetForm }) => {
    setIsLoading(true); // Start loading

    setTimeout(() => {
      const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(user),
      };

      fetch(`${BASEURL}/users`, requestOptions)
        .then(response => {
          setIsLoading(false); // Stop loading
          if (response.ok) {
            // Reset the form after a successful submission
            resetForm({ values: "" });
            // Redirect the user to another route
            redirect();
          } else {
            // Handle errors if the request was not successful
            console.error('Error:', response.statusText);
            // You can display an error message to the user or handle it as needed
          }
        })
        .catch(error => {
          setIsLoading(false); // Stop loading
          console.error('Network error:', error);
          // Handle network errors
          // You can display an error message to the user or handle it as needed
        });
    }, 1000); // Delay the fetch for 1000ms
  }

  return (
    <div>
      {isLoading ? (
        // Show a loading indicator while loading
        <div>Loading...</div>
      ) : (
        <UserForm submit={handleSubmit} />
      )}
    </div>
  );
}
