import { useRef } from "react";
import { Navigate, useNavigate } from "react-router-dom";

  
const url = "https://address-book-0wtp.onrender.com"
export const Login = () => {
  const idRef = useRef(null);
  const passRef = useRef(null);
  const navigate = useNavigate();
  const isLoggedIn = getIsLoggedInFlag();

  // Todo - If already logged in - then redirect to /members
  // dont show login page.
  if (isLoggedIn) {
    return <Navigate to={`/${TAB_IDS.MEMBERS}`}/>
  }

  const doLogin = () => {
    var raw = JSON.stringify({
      "phoneNumber": idRef.current.value,
      "password": passRef.current.value
    });

    var requestOptions = {
      method: 'POST',
      headers: {
        "Content-type": "application/json; charset=UTF-8"
      },
      body: raw
    };

    fetch(`${url}/login/v1`, requestOptions)
      .then(r => 
        
        {
          console.log(' r in 1st then ', r);
          if (r.status >= 200 && r.status <= 299) {
              return r.json();
          } else {
              throw ('Something went wrong');
          }
        }  
      )
      .then(r => {
        console.log('login response ', r);
        setAuthHeaders(r.phoneNumber, r.loginToken);
        navigate(`/${TAB_IDS.VIEW_USER}`);
        window.location.href = '/login'; // This is hack.. need to do bcz there is no centralised state.. Fix this
      })
      .catch(error => {
          // handle error by your self
          console.log('error', error);
      });
  };

  return (
    <div className="App">
        <div>
          <input ref={idRef} type='text' placeholder='enter phone number here' />
          &nbsp;&nbsp;&nbsp;
          <input ref={passRef} type='password' placeholder='enter password here' />
        </div>
        <br />
        <button type='button' onClick={doLogin}>Login</button>
    </div>
  );
}
