import { useState } from "react";
import { BASEURL } from "../services/api";
import { getIsLoggedInFlag } from "../helpers/auth";
import { useHistory } from "react-router-dom"; // Import useHistory for redirection

export const Login = () => {
    const [phonenumber, setPhonenumber] = useState('');
    const [password, setPassword] = useState('');
    const history = useHistory(); // Initialize useHistory for redirection

    const handleSubmit = async () => {
        const obj = {
            "phoneNumber": phonenumber,
            "password": password
        };

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(obj)
        };

        try {
            const response = await fetch(`${BASEURL}/login/v1`, requestOptions);
            if (response.ok) {
                const data = await response.json();
                // Assuming the server responds with a token in the "token" field
                const token = data.token;
                getIsLoggedInFlag(true); // Update the login flag
                // Redirect to the member view form or any desired route
                history.push('/member-form');
            } else {
                // Handle unsuccessful login here, show error messages, etc.
                console.log("Login failed");
            }
        } catch (error) {
            // Handle fetch or other errors
            console.error("An error occurred:", error);
        }
    };

    return (
        <>
            <div>
                <label htmlFor="phonenumber"> Phone Number:</label>
                <input
                    id="phonenumber"
                    type="text"
                    autoComplete="off"
                    value={phonenumber}
                    onChange={(e) => {
                        setPhonenumber(e.target.value);
                    }}
                />
            </div>
            &nbsp;&nbsp;
            <div>
                <label htmlFor="password"> PassWord:</label>
                <input
                    id="password"
                    type="password"
                    value={password}
                    onChange={(e) => {
                        setPassword(e.target.value);
                    }}
                />
            </div>
            <button onClick={handleSubmit}>
                Login
            </button>
        </>
    );
};
